urlset:
%for d in c.delegateables:

		<%
		controller = "d"
		if c.instance:
			if type(d) == c.model.Proposal:
				controller = "proposal"
			elif type(d) == c.model.Issue:
				controller = "issue"

		%>
		${h.instance_url(c.instance, path="/%s/%s" % (controller, d.id))}
    	${d.access_time.strftime("%Y-%m-%d")}
    	weekly
    	0.8

%endfor
